import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import PlayButton from './playButton';
import PauseButton from './pauseButton';
import SettingsButton from './settingsButton';
import { useContext,useState,useEffect, useRef} from 'react';
import SettingsContext from '../settingsContext';
import Nav from './nav';
import { updateTask } from '../firebase/firestore';


const red = '#f54e4e';
const green = '#4aec8c';

function Timer() {
    
    const settingsInfo = useContext(SettingsContext);
    const [isPaused,setIsPaused] = useState(true);
    const [mode, setMode] = useState('work') //work/break/null
    const [secondsLeft, setSecondsLeft] = useState(0);
    const secondsLeftRef = useRef(secondsLeft)
    const isPausedRef = useRef(isPaused)
    const modeRef = useRef(mode)
    const [tasks,setTasks] = useState([]);


    function initTimer(){
        secondsLeftRef.current = modeRef.current ==='work'? settingsInfo.workMinutes*60: settingsInfo.breakMinutes*60;
        setSecondsLeft(secondsLeftRef.current);
    }

    function switchMode(){
        const nextMode = modeRef.current ==='work'? 'break': 'work';
        const nextSeconds = nextMode === 'work' ? settingsInfo.workMinutes * 60 : settingsInfo.breakMinutes * 60
        setMode(nextMode);
        modeRef.current = nextMode;
        setSecondsLeft(nextSeconds);
        secondsLeftRef.current = nextSeconds;
    }
 
    async function beep() {
        var snd = new Audio("data:audio/wav;base64,");  
        snd.play();
    }

    function tick(){
        secondsLeftRef.current--;
        setSecondsLeft(secondsLeftRef.current)
    }

    useEffect(()=>{
        initTimer();

        const interval = setInterval(()=>{
            if(isPausedRef.current) return;

            if(secondsLeftRef.current === 0){

                 function updatetime(){
                let selecttag = document.querySelector('select')
                let selectedtask = selecttag.value
                    for(const task of tasks){
                        if (task.taskName === selectedtask){
                        let obj = {id: task.id, time: task.time + settingsInfo.workMinutes*60, taskName: task.taskName,uuid: task.uuid}
                        updateTask(obj)
                        break;
                    }
                }}
                
                if(modeRef.current==='work') updatetime();
                return switchMode();
            } 

            tick();
        },
        1000)
        return ()=> clearInterval(interval)
    },[settingsInfo,tasks])

    const totalSeconds = mode === 'work' ? settingsInfo.workMinutes*60 : settingsInfo.breakMinutes*60;
    const percentage = (secondsLeft/totalSeconds) *100;
    const minutes = Math.floor(secondsLeft/60);
    const height = window.screen.height*0.35;
    const width = window.screen.width*0.35;

    let seconds = secondsLeft % 60;
    if(seconds <10) seconds = '0' + seconds

    return (
        <div>
            <Nav tasks ={tasks} setTasks = {setTasks}/>
            <div style= {{height: height, width: width, paddingTop: '10px'}}>
            <CircularProgressbar value={percentage} text={minutes + ':' + seconds} styles={buildStyles({
                textColor: '#fff',
                pathColor: mode === 'work' ? red : green,
                tailColor: 'rgba(255,255,255,.2)',
            })} />
            </div>
            <div style= {{marginTop:height*0.1, display:'flex', alignItems: 'center', justifyContent: 'center'}}>
                {isPaused ? 
                <PlayButton onClick ={()=>{setIsPaused(false); isPausedRef.current = false;     beep();}} /> 
                : <PauseButton onClick ={()=>{setIsPaused(true); isPausedRef.current = true;    beep();}}/>}
          
            <SettingsButton onClick ={()=> settingsInfo.setShowSettings(true)}/>
        </div>
        </div>
    )
}

export default Timer;